```dart
import 'dart:collection';

// Sample transaction data
List<Transaction> transactions = [
  Transaction('BTC', now(), 50),
  Transaction('BTC', now().add(Duration(days: 1)), -20),
  Transaction('ETH', now(), 30),
  Transaction('ETH', now().add(Duration(days: 1)), -10),
  Transaction('LTC', now(), 20),
  Transaction('LTC', now().add(Duration(days: 1)), 5),
];

class Transaction {
  final String coin;
  final DateTime time;
  final int profit;

  Transaction(this.coin, this.time, this.profit);
}

Map<String, TransactionProfit> calculateProfit(List<Transaction> transactions) {
  var transactionProfits = {};

  for (var transaction in transactions) {
    var coin = transaction.coin;
    transactionProfits.putIfAbsent(
      coin,
      () => TransactionProfit(transaction),
    );
    transactionProfits[coin].addTransaction(transaction);
  }

  return transactionProfits;
}

class TransactionProfit {
  final List<Transaction> transactions;
  int totalProfit = 0;

  TransactionProfit(this.transactions);

  void addTransaction(Transaction transaction) {
    totalProfit += transaction.profit;
  }

  int get averageProfit => totalProfit ~/ transactions.length;
}

void main() {
  Map<String, TransactionProfit> profitMap = calculateProfit(transactions);

  List<String> top3HighestProfit = getTop3HighestProfit(profitMap);
  List<String> top3HighestLoss = getTop3HighestLoss(profitMap);

  print('Top 3 coins with highest average profit:');
  for (String coin in top3HighestProfit) {
    print('$coin: ${profitMap[coin].averageProfit}');
  }

  print('\nTop 3 coins with highest average loss:');
  for (String coin in top3HighestLoss) {
    print('$coin: ${-profitMap[coin].averageProfit}');
  }
}

List<String> getTop3HighestProfit(Map<String, TransactionProfit> profitMap) {
  List<String> top3HighestProfitCoins = [
    'BTC',
    'ETH',
    'LTC',
  ];

  if (profitMap.isNotEmpty) {
    top3HighestProfitCoins = profitMap.keys
        .map((coin) => coin)
        .sort((a, b) => profitMap[b].averageProfit - profitMap[a].averageProfit)
        .take(3);
  }
  return top3HighestProfitCoins;
}

List<String> getTop3HighestLoss(Map<String, TransactionProfit> profitMap) {
  List<String> top3HighestLossCoins = [
    'BTC',
    'ETH',
    'LTC',
  ];

  if (profitMap.isNotEmpty) {
    top3HighestLossCoins = profitMap.keys
        .map((coin) => coin)
        .sort((a, b) => -profitMap[b].averageProfit + profitMap[a].averageProfit)
        .take(3);
  }
  return top3HighestLossCoins;
}
```
This code defines the `calculateProfit` function to compute the total profit and average profit for each coin, and the `getTop3HighestProfit` and `getTop3HighestLoss` functions to return the top 3 coins with the highest and lowest average profit, respectively. The main function demonstrates how to use these functions with a sample list of transactions.

Please note that the solution assumes transactions for each coin and calculates the total and average profit for each.
